import React, { useState } from 'react'
import cn from 'classnames'

import Button from '../button'

import styles from './button-group.module.css'

import { SearchOutlined } from '@ant-design/icons';
import { Col, Row } from 'antd';

const ButtonGroup = ({
  buttons,
  selected,
  setSelected,
  borderBottom = false
}) => {
  const [showFilter, setShowFilter] = useState(false)

  return (
    <>
    <div className={cn(styles.container, borderBottom && styles.borderBottom)}>
      {buttons.map((button) => (
        <Button
          key={button}
          className={selected === button && styles.active}
          onClick={() => setSelected(button)}
        >
          {button}
        </Button>
      ))}
      <Button style={{marginLeft:20,  borderRadius: 3}} onClick={() => setShowFilter}>Filter</Button>
    </div>
    {showFilter} &&
      <Row style={{backgroundColor:"gray", paddingLeft: 30}}>
        <Col span={12}>col-12</Col>
        <Col span={12}>col-12</Col>
      </Row>
    </>
  )
}

export default ButtonGroup
